import "./ExpenseItem.css";

function ExpenseItem(props) {
  // const expenseDate = new Date(2022, 4, 23);
  // const expenseItem = "Car Insurance";
  // const expenseaAmount = 378.79;

  const year = props.date.toLocaleString("en-US", { month: "long" })
  const month = props.date.toLocaleString("en-US", {weekday: "long"})

  return (
    <div className="expense-item">
      <div>
        <div>{year}</div>
        <div>{month}</div>
        <div>{props.date.toLocaleString("en-US", {year: "numeric"})}</div>
      </div>
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price">${props.amount}</div>
      </div>
    </div>
  );
}

export default ExpenseItem;
